    if (isInvoking) {
      if (timerId === undefined) {
        return leadingEdge(lastCallTime);
      }
      if (maxing) {
        // Handle invocations in a tight loop.
        clearTimeout(timerId);
        timerId = setTimeout(timerExpired, wait);
        return invokeFunc(lastCallTime);
      }
    }
    if (timerId === undefined) {
      timerId = setTimeout(timerExpired, wait);
    }
    return result;
  }
  debounced.cancel = cancel;
  debounced.flush = flush;
  return debounced;
}

module.exports = debounce;
import {Snackbar} from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";
import React, {useEffect} from "react";
import Api from "../../helpers/api";
import './TLSWarning.sass';

const api = Api.getInstance();

interface TLSWarningProps {
    showTLSWarning: boolean
    setShowTLSWarning: (show: boolean) => void
    addressesWithTLS: Set<string>
    setAddressesWithTLS: (addresses: Set<string>) => void
    userDismissedTLSWarning: boolean
    setUserDismissedTLSWarning: (flag: boolean) => void
}

export const TLSWarning: React.FC<TLSWarningProps>  = ({showTLSWarning, setShowTLSWarning, addressesWithTLS, setAddressesWithTLS, userDismissedTLSWarning, setUserDismissedTLSWarning}) => {

    useEffect(() => {
        (async () => {
            try {
                const recentTLSLinks = await api.getRecentTLSLinks();
                if (recentTLSLinks?.length > 0) {
                    setAddressesWithTLS(new Set(recentTLSLinks));
                    setShowTLSWarning(true);
                }
            } catch (e) {
                console.error(e);
            }
        })();
    }, [setShowTLSWarning, setAddressesWithTLS]);

    return (<Snackbar open={showTLSWarning && !userDismissedTLSWarning}>
        <MuiAlert classes={{filledWarning: 'customWarningStyle'}} elevation={6} variant="filled"
                  onClose={() => setUserDismissedTLSWarning(true)} severity="warning">
            Mizu is detecting TLS traffic, this type of traffic will not be displayed.
            {addressesWithTLS.size > 0 &&
            <ul className="httpsDomains"> {Array.from(addressesWithTLS, address => <li>{address}</li>)} </ul>}
        </MuiAlert>
    </Snackbar>);
}
import { atom } from "recoil";

const trafficViewerApi = atom({
    key: "trafficViewerApi",
    default: {}
});

export default trafficViewerApi;
import React, { useCallback, useEffect, useMemo, useRef, useState } from "react";
import { Filters } from "../Filters";
import { EntriesList } from "../EntriesList";
import { makeStyles } from "@material-ui/core";
import TrafficViwerStyles from "./TrafficViewer.module.sass";
import styles from '../style/EntriesList.module.sass';
import {EntryDetailed} from "../EntryDetailed";
import playIcon from '../assets/run.svg';
import pauseIcon from '../assets/pause.svg';
import variables from '../../variables.module.scss';
import {StatusBar} from "../UI/StatusBar";
import { toast } from 'react-toastify';
import debounce from 'lodash/debounce';
import { RecoilRoot, RecoilState, useRecoilState, useRecoilValue, useSetRecoilState} from "recoil";
import tappingStatusAtom from "../../recoil/tappingStatus";
import entriesAtom from "../../recoil/entries";
import focusedEntryIdAtom from "../../recoil/focusedEntryId";
import websocketConnectionAtom, {WsConnectionStatus} from "../../recoil/wsConnection";
import queryAtom from "../../recoil/query";
import {TLSWarning} from "../TLSWarning/TLSWarning";
import { closeWsConnectionCallbackAtom } from "../../recoil/wsConnection/atom";
import trafficViewerApiAtom from "../../recoil/TrafficViewerApi"


const useLayoutStyles = makeStyles(() => ({
  details: {
    flex: "0 0 50%",
    width: "45vw",
    padding: "12px 24px",
    borderRadius: 4,
    marginTop: 15,
    background: variables.headerBackgroundColor,
  },

  viewer: {
    display: "flex",
    overflowY: "auto",
    height: "calc(100% - 70px)",
    padding: 5,
    paddingBottom: 0,
    overflow: "auto",
  },
}));

type trafficViewerApi = {
  validateQuery : (query: any) => any
  tapStatus : () => any
  analyzeStatus : () => any
  fetchEntries : (leftOff: any, direction: number, query: any, limit: number, timeoutMs: number) => any
  getEntry : (entryId : any) => any
  getRecentTLSLinks : () => any
}

interface TrafficViewerProps {
  setAnalyzeStatus?: (status: any) => void;
  api? :any
  message :{}
  error? :{}
  isOpen : boolean
  closeWs : () => void
  sendQuery : (query : string) => void
  openSocket : () => void
  trafficViewerApiProp : trafficViewerApi
}

const TrafficViewer: React.FC<TrafficViewerProps> = ({setAnalyzeStatus, message, isOpen, closeWs, sendQuery, openSocket, trafficViewerApiProp}) => {
    const classes = useLayoutStyles();
    const [tappingStatus, setTappingStatus] = useRecoilState(tappingStatusAtom);
    const [entries, setEntries] = useRecoilState(entriesAtom);
    const [focusedEntryId, setFocusedEntryId] = useRecoilState(focusedEntryIdAtom);
    const [wsConnection, setWsConnection] = useRecoilState(websocketConnectionAtom);
    const setCloseWsCallback = useSetRecoilState(closeWsConnectionCallbackAtom)
    const query = useRecoilValue(queryAtom);
    const [queryToSend, setQueryToSend] = useState("")
    const [trafficViewerApiState, setTrafficViewerApiState] = useRecoilState(trafficViewerApiAtom as RecoilState<trafficViewerApi>)


    const [noMoreDataTop, setNoMoreDataTop] = useState(false);
    const [isSnappedToBottom, setIsSnappedToBottom] = useState(true);

    const [queryBackgroundColor, setQueryBackgroundColor] = useState("#f5f5f5");

    const [queriedCurrent, setQueriedCurrent] = useState(0);
    const [queriedTotal, setQueriedTotal] = useState(0);
    const [leftOffBottom, setLeftOffBottom] = useState(0);
    const [leftOffTop, setLeftOffTop] = useState(null);
    const [truncatedTimestamp, setTruncatedTimestamp] = useState(0);

    const [startTime, setStartTime] = useState(0);
    const scrollableRef = useRef(null);

    const [showTLSWarning, setShowTLSWarning] = useState(false);
    const [userDismissedTLSWarning, setUserDismissedTLSWarning] = useState(false);
    const [addressesWithTLS, setAddressesWithTLS] = useState(new Set<string>());

    const handleQueryChange = useMemo(
      () =>
        debounce(async (query: string) => {
          if (!query) {
            setQueryBackgroundColor("#f5f5f5");
          } else {
            const data = await trafficViewerApiProp.validateQuery(query);
            if (!data) {
              return;
            }
            if (data.valid) {
              setQueryBackgroundColor("#d2fad2");
            } else {
              setQueryBackgroundColor("#fad6dc");
            }
          }
        }, 500),
      []
    ) as (query: string) => void;

    useEffect(() => {
        handleQueryChange(query);
    }, [query, handleQueryChange]);


    const listEntry = useRef(null);
    const openWebSocket = (query: string, resetEntries: boolean) => {
        if (resetEntries) {
            setFocusedEntryId(null);
            setEntries([]);
            setQueriedCurrent(0);
            setLeftOffTop(null);
            setNoMoreDataTop(false);
        }
        setQueryToSend(query)
        openSocket();
    }

    const onmessage = useCallback((e) => {
      if (!e?.data) return;
      const message = JSON.parse(e.data);
      switch (message.messageType) {
        case "entry":
          const entry = message.data;
          if (!focusedEntryId) setFocusedEntryId(entry.id.toString());
          const newEntries = [...entries, entry];
          if (newEntries.length === 10001) {
            setLeftOffTop(newEntries[0].entry.id);
            newEntries.shift();
            setNoMoreDataTop(false);
          }
          setEntries(newEntries);
          break;
        case "status":
          setTappingStatus(message.tappingStatus);
          break;
        case "analyzeStatus":
          setAnalyzeStatus(message.analyzeStatus);
          break;
        case "outboundLink":
          onTLSDetected(message.Data.DstIP);
          break;
        case "toast":
          toast[message.data.type](message.data.text, {
            position: "bottom-right",
            theme: "colored",
            autoClose: message.data.autoClose,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
          break;
        case "queryMetadata":
          setQueriedCurrent(queriedCurrent + message.data.current);
          setQueriedTotal(message.data.total);
          setLeftOffBottom(message.data.leftOff);
          setTruncatedTimestamp(message.data.truncatedTimestamp);
          if (leftOffTop === null) {
            setLeftOffTop(message.data.leftOff - 1);
          }
          break;
        case "startTime":
          setStartTime(message.data);
          break;
        default:
          console.error(
            `unsupported websocket message type, Got: ${message.messageType}`
          );
      }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[message]);

    useEffect(() =>{
      onmessage(message)
    },[message, onmessage])


    useEffect(()=>{
      isOpen ? setWsConnection(WsConnectionStatus.Connected) : setWsConnection(WsConnectionStatus.Closed)
      sendQuery(queryToSend)
    },[isOpen, queryToSend, sendQuery, setWsConnection])


    // const onerror = (event) => {
    //     console.error("WebSocket error:", event);
    //     if (query) {
    //         openWebSocket(`(${query}) and leftOff(${leftOffBottom})`, false);
    //     } else {
    //         openWebSocket(`leftOff(${leftOffBottom})`, false);
    //     }
    // }

    useEffect(() => {(async () => {
        setTrafficViewerApiState(trafficViewerApiProp)
        openWebSocket("leftOff(-1)", true);
        try{
            const tapStatusResponse = await trafficViewerApiProp.tapStatus();
            setTappingStatus(tapStatusResponse);
            if(setAnalyzeStatus) {
                const analyzeStatusResponse = await trafficViewerApiProp.analyzeStatus();
                setAnalyzeStatus(analyzeStatusResponse);
            }
        } catch (error) {
            console.error(error);
        }
      })()
    // eslint-disable-next-line
    }, []);


    const closeCallback = () => {wsConnection === WsConnectionStatus.Connected && closeWs()}
    setCloseWsCallback({closeCallback : closeCallback})

    const toggleConnection = () => {  
      if (wsConnection === WsConnectionStatus.Closed) {

          if (query) {
              openWebSocket(`(${query}) and leftOff(-1)`, true);
          } else {
              openWebSocket(`leftOff(-1)`, true);
          }
          scrollableRef.current.jumpToBottom();
          setIsSnappedToBottom(true);
      }
      else if(wsConnection === WsConnectionStatus.Connected)
        {
          closeWs()
          setWsConnection(WsConnectionStatus.Closed);
        }
    }

    const onTLSDetected = (destAddress: string) => {
        addressesWithTLS.add(destAddress);
        setAddressesWithTLS(new Set(addressesWithTLS));

        if (!userDismissedTLSWarning) {
            setShowTLSWarning(true);
        }
    };

    const getConnectionStatusClass = (isContainer) => {
        const container = isContainer ? "Container" : "";
        switch (wsConnection) {
            case WsConnectionStatus.Connected:
                return "greenIndicator" + container;
            default:
                return "redIndicator" + container;
        }
    }

    const getConnectionTitle = () => {
        switch (wsConnection) {
            case WsConnectionStatus.Connected:
                return "streaming live traffic"
            default:
                return "streaming paused";
        }
    }

    const onSnapBrokenEvent = () => {
        setIsSnappedToBottom(false);
        if (wsConnection === WsConnectionStatus.Connected) {
          closeWs()
        }
    }




  return (
    <div className={TrafficViwerStyles.TrafficPage}>
      <div className={TrafficViwerStyles.TrafficPageHeader}>
        <div className={TrafficViwerStyles.TrafficPageStreamStatus}>
          <img className={TrafficViwerStyles.playPauseIcon} style={{ visibility: wsConnection === WsConnectionStatus.Connected ? "visible" : "hidden" }} alt="pause"
            src={pauseIcon} onClick={toggleConnection} />
          <img className={TrafficViwerStyles.playPauseIcon} style={{ position: "absolute", visibility: wsConnection === WsConnectionStatus.Connected ? "hidden" : "visible" }} alt="play"
            src={playIcon} onClick={toggleConnection} />
          <div className={TrafficViwerStyles.connectionText}>
            {getConnectionTitle()}
            <div className={`${TrafficViwerStyles.indicatorContainer} ` + getConnectionStatusClass(true)}>
              <div className={`${TrafficViwerStyles.indicator} ` + getConnectionStatusClass(false)} />
            </div>
          </div>
        </div>
      </div>
      {<div className={TrafficViwerStyles.TrafficPageContainer}>
        <div className={TrafficViwerStyles.TrafficPageListContainer}>
          <Filters
            backgroundColor={queryBackgroundColor}
            openWebSocket={openWebSocket}

          />
          <div className={styles.container}>
            <EntriesList
              listEntryREF={listEntry}
              onSnapBrokenEvent={onSnapBrokenEvent}
              isSnappedToBottom={isSnappedToBottom}
              setIsSnappedToBottom={setIsSnappedToBottom}
              queriedCurrent={queriedCurrent}
              setQueriedCurrent={setQueriedCurrent}
              queriedTotal={queriedTotal}
              setQueriedTotal={setQueriedTotal}
              startTime={startTime}
              noMoreDataTop={noMoreDataTop}
              setNoMoreDataTop={setNoMoreDataTop}
              leftOffTop={leftOffTop}
              setLeftOffTop={setLeftOffTop}
              openWebSocket={openWebSocket}
              leftOffBottom={leftOffBottom}
              truncatedTimestamp={truncatedTimestamp}
              setTruncatedTimestamp={setTruncatedTimestamp}
              scrollableRef={scrollableRef}
            />
          </div>
        </div>
        <div className={classes.details} id="rightSideContainer">
          {focusedEntryId && <EntryDetailed />}
        </div>
      </div>}
      {tappingStatus && <StatusBar />}
       <TLSWarning showTLSWarning={showTLSWarning}
                   setShowTLSWarning={setShowTLSWarning}
                   addressesWithTLS={addressesWithTLS}
                   setAddressesWithTLS={setAddressesWithTLS}
                   userDismissedTLSWarning={userDismissedTLSWarning}
                   setUserDismissedTLSWarning={setUserDismissedTLSWarning} />
    </div>
  );
};

const MemoiedTrafficViwer =  React.memo(TrafficViewer)
const TrafficViewerContainer : React.FC<TrafficViewerProps> = ({setAnalyzeStatus, message, isOpen, closeWs, sendQuery, openSocket, trafficViewerApiProp}) => 
{
  return <RecoilRoot>
    <MemoiedTrafficViwer message={message} isOpen={isOpen} closeWs={closeWs} sendQuery={sendQuery } openSocket={ openSocket}
                       trafficViewerApiProp={trafficViewerApiProp} setAnalyzeStatus={setAnalyzeStatus}/>
  </RecoilRoot>
}


export default TrafficViewerContainer 
import React, {useEffect, useState} from "react";
import style from './style/LoadingOverlay.module.sass';

const SpinnerShowDelayMs = 350;

interface LoadingOverlayProps {
    delay?: number
}

const LoadingOverlay: React.FC<LoadingOverlayProps> = ({delay}) => {

    const [isVisible, setIsVisible] = useState(false);

    // @ts-ignore
    useEffect(() => {
        let isRelevant = true;

        setTimeout(() => {
            if(isRelevant)
                setIsVisible(true);
        }, delay || SpinnerShowDelayMs);

        return () => isRelevant = false;
    }, [delay]);

    return <div className={style.loadingOverlayContainer} hidden={!isVisible}>
        <div className={style.loadingOverlaySpinner}/>
    </div>
};

export default LoadingOverlay;
